utils.c
1 #include &lt;stdio.h&gt;
2 #include &lt;stdlib.h&gt;
3 #include &lt;string.h&gt;
4 #include &lt;math.h&gt;
5 #include &ldquo;utils.h&rdquo;
6 7 //字符串逆序
8 char * strReverse(char str)
9 {
10 int i,j,len = strlen(str);
11 char * res = (char)malloc(len
1);
12 memset(res, 0, len + 1);
13 for(i = len - 1, j = 0; i &gt;= 0; i --, j ++) {
14 res[j] = str[i];
15 }
16 return res;
17 }
18 19 //2，16进制转10进制
20 int CommontoD(char *pC, int factor)
21 {
22 pC = strReverse(pC);
23 int d_res = 0;
24 int d_num = 0;
25 int i;
26 for(i = strlen(pC) - 1; i &gt;= 0; i --)
27 {
28 if(pC[i] &gt;= &lsquo;0&rsquo; &amp;&amp; pC[i] &lt;= &lsquo;9&rsquo;) {
29 d_num = pC[i] - &lsquo;0&rsquo;;
30 }
31 if(pC[i] &gt;= &lsquo;a&rsquo; &amp;&amp; pC[i] &lt;= &lsquo;f&rsquo;) {
32 d_num = pC[i] - 87;
33 }
34 if(pC[i] &gt;= &lsquo;A&rsquo; &amp;&amp; pC[i] &lt;= &lsquo;F&rsquo;) {
35 d_num = pC[i] - 55;
36 }
37 d_res += d_num * pow(factor, i);
38 }
39 return d_res;
40 }
41 42 //将10进制数转为字符串
43 char * DtoDstr(int d_num)
44 {
45 char d_str = (char)malloc(6);
46 memset(d_str,0,6);
47 int i = 0;
48 while(d_num &gt; 0)
49 {
50 d_str[i++] = (d_num % 10) + &lsquo;0&rsquo;;
51 d_num /= 10;
52 }
53 return strReverse(d_str);
54 }
55 //二进制到十进制
56 char * BtoD(char *pB)
57 {
58 return DtoDstr(CommontoD(pB, B));
59 }
60 61 //十六进制到十进制
62 char * HtoD(char *pH)
63 {
64 return DtoDstr(CommontoD(pH, H));
65 }
66 67 68 //十进制到二进制
69 char * DtoB(char *pD)
70 {
71 //65535用16个1表示
72 //开17个就够了
73 char b_str = (char)malloc(17);
74 memset(b_str,0,17);
75 int d_num = atoi(pD);
76 int i = 0;
77 while(d_num &gt; 0) {
78 b_str[i++] = (d_num % 2) + &lsquo;0&rsquo;;
79 d_num /= 2;
80 }
81 return strReverse(b_str);
82 }
83 84 //十六进制转二进制
85 char * HtoB(char *pH)
86 {
87 return DtoB(HtoD(pH));
88 }
89 90 //十进制到十六进制
91 char * DtoH(char *pD)
92 {
93 //65535用4个f表示
94 //开5个就够了
95 char h_str = (char)malloc(5);
96 memset(h_str,0,5);
97 int d_num = atoi(pD);
98 int bit;
99 int i = 0;
100 while(d_num &gt; 0) {
101 bit = d_num % 16;
102 if(bit &gt;= 0 &amp;&amp; bit &lt;= 9) {
103 h_str[i++] = bit + &lsquo;0&rsquo;;
104 }
105 else {
106 h_str[i++] = bit + 87;
107 }
108 d_num /= 16;
109 }
110 return strReverse(h_str);
111 }
112 113 //二进制转十六进制
114 char * BtoH(char *pB)
115 {
116 return DtoH(BtoD(pB));
117 }
118 119 //检查16进制数是否合法
120 int HFormatCheck(char *pH)
121 {
122 if(pH == NULL) {
123 return RD_FALSE;
124 }
125 126 int len = strlen(pH);
127 int i;
128 for(i = 0; i &lt; len; i ++)
129 {
130 if(!((pH[i] &gt;= &lsquo;0&rsquo; &amp;&amp; pH[i] &lt;= &lsquo;9&rsquo;) || (pH[i] &gt;= &lsquo;a&rsquo; &amp;&amp; pH[i] &lt;= &lsquo;f&rsquo;) || (pH[i] &gt;= &lsquo;A&rsquo; &amp;&amp; pH[i] &lt;= &lsquo;F&rsquo;) ))
131 {
132 return RD_FALSE;
133 }
134 }
135 return RD_TRUE;
136 }
