147 {
148 if(!(pH[i] &gt;= &lsquo;0&rsquo; &amp;&amp; pH[i] &lt;= &lsquo;1&rsquo;))
149 {
150 return RD_FALSE;
151 }
152 }
153 return RD_TRUE;
154 }
